package ForLoop;

public class EnhancedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]={2,11,45,9};
	      for (int num : arr) {
	         System.out.println(num);
	      }
	      String sarr[]={"hi","hello","bye"};
	      for (String str : sarr) {
	               System.out.println(str);
	      }
	}

}
