package ForLoop;

public class IterateArrayForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int arr[]={2,11,45,9};
         //i starts with 0 as array index starts with 0 too
         for(int i=0; i<arr.length; i++){
              System.out.println(arr[i]);
         }
	}

}
