package JavaBasic;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2;
		num1 = 25;

		num2 = (num1 == 10) ? 100 : 200;
		System.out.println("num2: " + num2);

		num2 = (num1 == 25) ? 100 : 200;
		System.out.println("num2: " + num2);
	}

}
